package practice.leetcode.slidingWindow;

import java.util.HashMap;
import java.util.Map;

//https://leetcode.com/problems/permutation-in-string/description/
public class PermutationInString {
    public static void main(String[] args) {
        test("ab", "eidbaooo", true);
        test("ab", "eidboaoo", false);
        test("mtpu", "computer", true);
        test("cmtpou", "computer", true);
        test("ana", "banana", true);
        test("abna", "banana", true);
        test("sabna", "banana", false);
        test("absna", "bananas", false);
        test("abana", "bananas", false);
        test("asana", "bananas", false);
        test("asan", "bananas", true);
        test("nasan", "bananas", true);
        test("adc", "dcda", true);
        test("ccc", "cbac", false);
        test("ab", "ba", true);
        test("ab", "ab", true);
        test("ab", "a", false);
        test("a", "ab", true);
        test("", "", true);
    }

    private static void test(String s1, String s2, boolean expected) {
        boolean myAnswer = checkInclusion(s1, s2);
        String finalAnswer = myAnswer == expected ? "✅" : "❌";
        System.out.printf("s1: %s, s2: %s, Result: %s, Expected: %s, Final: %s.\n", s1, s2, myAnswer, expected, finalAnswer);
    }

    public static boolean checkInclusion(String s1, String s2) {

        if (s1.length() <= s2.length()) {
            return checkUsingMap(s1, s2);
        }
        return false;
    }

    private static boolean checkUsingMap(String s1, String s2) {
        Map<Character, Integer> mapS1 = new HashMap<>();
        Map<Character, Integer> mapS2 = new HashMap<>();

        letterFrequencyMap(s1, s1.length(), mapS1);

        int left = 0;
        int right = left + s1.length() - 1;

        while (left <= right && right < s2.length()) {
            if (mapS2.isEmpty()) {
                letterFrequencyMap(s2, s1.length(), mapS2);
            }
            else if (mapS1.equals(mapS2)) {
                return true;
            }
            else {
                char s2l = s2.charAt(left);

                if (mapS2.containsKey(s2l)) {
                    int count = mapS2.get(s2l);
                    if (count > 1) {
                        mapS2.put(s2l, count - 1);
                    }
                    else {
                        mapS2.remove(s2l);
                    }
                }

                left++;
                right = left + s1.length() - 1;
                if (right >= s2.length()) {
                    return false;
                }
                char s2r = s2.charAt(right);

                if (mapS2.containsKey(s2r)) {
                    mapS2.put(s2r, mapS2.get(s2r) + 1);
                }
                else {
                    mapS2.put(s2r, 1);
                }
            }
        }
        return false;
    }

    private static void letterFrequencyMap(String string, int len, Map<Character, Integer> map) {
        for (int i = 0; i < len; i++) {
            int count = 0;
            if (map.containsKey(string.charAt(i))) {
                count = map.get(string.charAt(i));
            }
            map.put(string.charAt(i), count + 1);
        }
    }

}
